import{_ as s,c as e,o as n,ae as t}from"./chunks/framework.vWxN6zjL.js";const h=JSON.parse('{"title":"AI Integration Overview","description":"","frontmatter":{},"headers":[],"relativePath":"ai/overview.md","filePath":"ai/overview.md"}'),r={name:"ai/overview.md"};function i(l,a,o,p,d,c){return n(),e("div",null,[...a[0]||(a[0]=[t(`<h1 id="ai-integration-overview" tabindex="-1">AI Integration Overview <a class="header-anchor" href="#ai-integration-overview" aria-label="Permalink to &quot;AI Integration Overview&quot;">​</a></h1><p>Alphabet Soup uses AI to extract word search puzzles from images. Users can take a photo of a printed puzzle and have it converted to text format automatically.</p><h2 id="why-ai" tabindex="-1">Why AI? <a class="header-anchor" href="#why-ai" aria-label="Permalink to &quot;Why AI?&quot;">​</a></h2><p>Word search puzzles often come as:</p><ul><li>Printed worksheets</li><li>Magazine pages</li><li>PDFs</li><li>Screenshots</li></ul><p>Manually typing in a 15x15 grid is tedious and error-prone. AI vision models can read the image and extract the letter grid and word list in seconds.</p><h2 id="architecture" tabindex="-1">Architecture <a class="header-anchor" href="#architecture" aria-label="Permalink to &quot;Architecture&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────────────────────────────────────┐</span></span>
<span class="line"><span>│                         Browser                                  │</span></span>
<span class="line"><span>│  ┌─────────────┐    ┌──────────────┐    ┌──────────────────┐   │</span></span>
<span class="line"><span>│  │  AIUpload   │───▶│ IPuzzleParser│───▶│ PuzzleService    │   │</span></span>
<span class="line"><span>│  │  Component  │    │     Api      │    │ (fires event)    │   │</span></span>
<span class="line"><span>│  └─────────────┘    └──────────────┘    └──────────────────┘   │</span></span>
<span class="line"><span>│         │                                                        │</span></span>
<span class="line"><span>└─────────│────────────────────────────────────────────────────────┘</span></span>
<span class="line"><span>          │ HTTPS</span></span>
<span class="line"><span>          ▼</span></span>
<span class="line"><span>┌─────────────────────────────────────────────────────────────────┐</span></span>
<span class="line"><span>│              Serverless Functions (Cloud Platform)               │</span></span>
<span class="line"><span>│  ┌──────────────────┐         ┌─────────────────────────────┐  │</span></span>
<span class="line"><span>│  │ Cloudflare Worker│   OR    │     Vercel Function         │  │</span></span>
<span class="line"><span>│  │  (Holds API keys)│         │    (Holds API keys)         │  │</span></span>
<span class="line"><span>│  └────────┬─────────┘         └──────────────┬──────────────┘  │</span></span>
<span class="line"><span>└───────────│──────────────────────────────────│──────────────────┘</span></span>
<span class="line"><span>            │                                   │</span></span>
<span class="line"><span>            ▼                                   ▼</span></span>
<span class="line"><span>┌───────────────────────────────────────────────────────────────────┐</span></span>
<span class="line"><span>│                        AI Vision APIs                              │</span></span>
<span class="line"><span>│   ┌─────────┐   ┌─────────┐   ┌──────────┐   ┌──────────────┐   │</span></span>
<span class="line"><span>│   │  Groq   │   │ Gemini  │   │ Together │   │ Cloudflare   │   │</span></span>
<span class="line"><span>│   │ (Llama) │   │(Google) │   │   AI     │   │   Workers AI │   │</span></span>
<span class="line"><span>│   └─────────┘   └─────────┘   └──────────┘   └──────────────┘   │</span></span>
<span class="line"><span>└───────────────────────────────────────────────────────────────────┘</span></span></code></pre></div><h2 id="how-it-works" tabindex="-1">How It Works <a class="header-anchor" href="#how-it-works" aria-label="Permalink to &quot;How It Works&quot;">​</a></h2><ol><li><strong>User uploads image</strong> via AIUpload component</li><li><strong>Browser sends image</strong> to serverless function as base64</li><li><strong>Serverless function</strong> adds API key and calls AI provider</li><li><strong>AI provider</strong> analyzes image and returns structured text</li><li><strong>Response flows back</strong> to browser</li><li><strong>Component parses response</strong> into puzzle format</li></ol><h2 id="why-serverless-functions" tabindex="-1">Why Serverless Functions? <a class="header-anchor" href="#why-serverless-functions" aria-label="Permalink to &quot;Why Serverless Functions?&quot;">​</a></h2><p><strong>Security:</strong> API keys must be kept secret. If we called AI APIs directly from the browser, the keys would be visible in network requests.</p><p><strong>CORS:</strong> Most AI APIs don&#39;t allow browser requests. Serverless functions act as a proxy.</p><p><strong>Rate Limiting:</strong> Server-side code can implement rate limiting to prevent abuse.</p><h2 id="data-flow" tabindex="-1">Data Flow <a class="header-anchor" href="#data-flow" aria-label="Permalink to &quot;Data Flow&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Image (bytes)</span></span>
<span class="line"><span>    → Base64 encode</span></span>
<span class="line"><span>    → JSON request</span></span>
<span class="line"><span>    → Serverless function</span></span>
<span class="line"><span>    → AI API</span></span>
<span class="line"><span>    → Structured text response</span></span>
<span class="line"><span>    → Parse to puzzle format</span></span>
<span class="line"><span>    → Display in UI</span></span></code></pre></div><h2 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-label="Permalink to &quot;Configuration&quot;">​</a></h2><p>The user selects:</p><ol><li><p><strong>Platform</strong> - Which serverless provider to use</p><ul><li>Cloudflare Workers</li><li>Vercel Functions</li></ul></li><li><p><strong>AI Provider</strong> - Which AI model to use</p><ul><li>Groq (Llama 3.2 Vision)</li><li>Google Gemini</li><li>Together AI</li><li>Cloudflare Workers AI (Cloudflare only)</li></ul></li></ol><h2 id="error-handling" tabindex="-1">Error Handling <a class="header-anchor" href="#error-handling" aria-label="Permalink to &quot;Error Handling&quot;">​</a></h2><p>Common failure scenarios:</p><table tabindex="0"><thead><tr><th>Error</th><th>Cause</th><th>User Action</th></tr></thead><tbody><tr><td>&quot;Invalid image&quot;</td><td>Unsupported format</td><td>Use PNG, JPG, or WebP</td></tr><tr><td>&quot;Rate limited&quot;</td><td>Too many requests</td><td>Wait and try again</td></tr><tr><td>&quot;Parse failed&quot;</td><td>AI couldn&#39;t read image</td><td>Try different provider</td></tr><tr><td>&quot;Network error&quot;</td><td>Connection issue</td><td>Check internet</td></tr></tbody></table><h2 id="learn-more" tabindex="-1">Learn More <a class="header-anchor" href="#learn-more" aria-label="Permalink to &quot;Learn More&quot;">​</a></h2><ul><li><a href="./platforms.html">Platforms</a> - Detailed platform documentation</li><li><a href="./services.html">AI Services</a> - AI provider comparison</li></ul>`,24)])])}const g=s(r,[["render",i]]);export{h as __pageData,g as default};
