import{_ as a,c as t,o as n,ae as r}from"./chunks/framework.vWxN6zjL.js";const u=JSON.parse('{"title":"Architecture Overview","description":"","frontmatter":{},"headers":[],"relativePath":"architecture.md","filePath":"architecture.md"}'),s={name:"architecture.md"};function i(o,e,l,d,c,p){return n(),t("div",null,[...e[0]||(e[0]=[r(`<h1 id="architecture-overview" tabindex="-1">Architecture Overview <a class="header-anchor" href="#architecture-overview" aria-label="Permalink to &quot;Architecture Overview&quot;">​</a></h1><p>Alphabet Soup follows <strong>Clean Architecture</strong> principles, organizing code into four distinct layers. Each layer has a specific purpose and set of rules about what it can and cannot do.</p><h2 id="the-four-layers" tabindex="-1">The Four Layers <a class="header-anchor" href="#the-four-layers" aria-label="Permalink to &quot;The Four Layers&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────────────────────────┐</span></span>
<span class="line"><span>│                    Web Layer                        │</span></span>
<span class="line"><span>│         (Blazor components, pages, UI)              │</span></span>
<span class="line"><span>├─────────────────────────────────────────────────────┤</span></span>
<span class="line"><span>│              Infrastructure Layer                   │</span></span>
<span class="line"><span>│    (HTTP clients, file I/O, external services)      │</span></span>
<span class="line"><span>├─────────────────────────────────────────────────────┤</span></span>
<span class="line"><span>│               Application Layer                     │</span></span>
<span class="line"><span>│      (Use cases, interfaces, orchestration)         │</span></span>
<span class="line"><span>├─────────────────────────────────────────────────────┤</span></span>
<span class="line"><span>│                 Domain Layer                        │</span></span>
<span class="line"><span>│    (Entities, value objects, business rules)        │</span></span>
<span class="line"><span>└─────────────────────────────────────────────────────┘</span></span></code></pre></div><h2 id="dependency-rule" tabindex="-1">Dependency Rule <a class="header-anchor" href="#dependency-rule" aria-label="Permalink to &quot;Dependency Rule&quot;">​</a></h2><p><strong>Dependencies only point inward.</strong> This means:</p><ul><li>Domain knows nothing about Application, Infrastructure, or Web</li><li>Application knows about Domain, but not Infrastructure or Web</li><li>Infrastructure knows about Application and Domain</li><li>Web knows about everything</li></ul><p>This rule keeps the core business logic (Domain) independent of frameworks and external services.</p><h2 id="layer-summary" tabindex="-1">Layer Summary <a class="header-anchor" href="#layer-summary" aria-label="Permalink to &quot;Layer Summary&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Layer</th><th>Project</th><th>Purpose</th></tr></thead><tbody><tr><td>Domain</td><td><code>WordSearch.Domain</code></td><td>Core business objects and algorithms</td></tr><tr><td>Application</td><td><code>WordSearch.Application</code></td><td>Interfaces and simple services</td></tr><tr><td>Infrastructure</td><td><code>WordSearch.Infrastructure</code></td><td>External API implementations</td></tr><tr><td>Web</td><td><code>WordSearch.Web</code></td><td>Blazor UI and user interaction</td></tr></tbody></table><h2 id="when-to-add-code" tabindex="-1">When to Add Code <a class="header-anchor" href="#when-to-add-code" aria-label="Permalink to &quot;When to Add Code&quot;">​</a></h2><p><strong>Ask yourself:</strong> &quot;What is this code responsible for?&quot;</p><table tabindex="0"><thead><tr><th>If the code...</th><th>Put it in...</th></tr></thead><tbody><tr><td>Defines what a puzzle IS</td><td>Domain</td></tr><tr><td>Defines the algorithm to SOLVE a puzzle</td><td>Domain</td></tr><tr><td>Defines HOW to get a puzzle from an external source</td><td>Infrastructure</td></tr><tr><td>Defines the UI for displaying a puzzle</td><td>Web</td></tr><tr><td>Orchestrates multiple operations</td><td>Application</td></tr></tbody></table><h2 id="learn-more" tabindex="-1">Learn More <a class="header-anchor" href="#learn-more" aria-label="Permalink to &quot;Learn More&quot;">​</a></h2><ul><li><a href="./architecture/domain.html">Domain Layer</a> - Entities, algorithms, and business rules</li><li><a href="./architecture/application.html">Application Layer</a> - Interfaces and services</li><li><a href="./architecture/infrastructure.html">Infrastructure Layer</a> - External API implementations</li><li><a href="./architecture/web.html">Web Layer</a> - Blazor components and pages</li></ul>`,15)])])}const m=a(s,[["render",i]]);export{u as __pageData,m as default};
